"use strict";

// Imports
const isNumber = require("./isNumber.js");

/**
 * Returns the decimal value of a number-like value
 * @param {string|number} v Number-like value
 * @returns {string}
 */
function decimal(v) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    return `${v}`.match(/(?<=(^\+|^-|^)(\d*?\.|\d+\.))\d*$/)[0];
};

// Exports
module.exports = decimal;