"use strict";

// Imports
const isNumber = require("./isNumber.js");
const parse = require("../utils/parse.js");

/**
 * Returns the integer value of a number-like value
 * @param {string|number} v Number-like value
 * @param {boolean} s Whether or not to include sign
 * @returns {string}
 */
function integer(v, s = true) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    return s ? parse(v).slice(0, 2).join("") : `${v}`.match(/(?<=^\+|^-|^)\d*(?=\.?\d*$|$)/)[0];
};

// Exports
module.exports = integer;