"use strict";

// Imports
const isNumber = require("./isNumber.js");
const isPositive = require("./isPositive.js");
const negative = require("./negative.js");
const positive = require("./positive.js");

/**
 * Inverts the sign of a number-like value
 * @param {string} v Number-like value
 * @returns {string}
 */
function invert(v) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    return isPositive(v) ? negative(v) : positive(v);
};

// Exports
module.exports = invert;