"use strict";

/**
 * Returns if a number-like value is an integer
 * @param {string|number} v Number-like value
 * @param {boolean} s Strict mode
 * @returns {boolean}
 */
function isInteger(v, s) {
    return (s ? /^[+-]?(\d+\.?)$/ : /^[+-]?(\d+\.?|\d*\.0+)$/).test(`${v}`);
};

// Exports
module.exports = isInteger;