"use strict";

// Imports
const isNumber = require("./isNumber.js");
const sign = require("./sign.js");

/**
 * Returns if a number-like value is negative
 * @param {string|number} v Number-like value
 * @returns {boolean}
 */
function isNegative(v) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    return sign(v) === "-";
};

// Exports
module.exports = isNegative;