"use strict";

// Imports
const compare = require("./compare.js");
const isNumber = require("./isNumber.js");

/**
 * Returns the smallest number-like value in the array
 * @param {(string|number)[]} av Array of number-like values
 * @returns {string}
 */
function minimum(av) {
    if(!Array.isArray(av)) throw new TypeError("Arugment is not an array");
    if(av.length === 0) throw new Error("One or more arguments must be passed");
    if(!av.every(v => isNumber(v))) throw new TypeError("Argument is not a number-like value");
    return av.reduce((a, c) => compare(a, c) === 1 ? c : a);
};

// Exports
module.exports = minimum;