"use strict";

// Imports
const isNumber = require("./isNumber.js");
const format = require("../utils/format.js");
const parse = require("../utils/parse.js");

/**
 * Returns the negative value of a number-like value
 * @param {string|number} v Number-like value
 * @param {boolean} t Whether or not to remove trim zeros
 * @returns {string}
 */
function negative(v, t) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    return format([ "-", ...parse(v, t).slice(1) ]);
};

// Exports
module.exports = negative;