"use strict";

// Imports
const gdivide = require("./gdivide.js");

/**
 * Returns the modulus of two number-like values
 * @param {string|number} a Number-like value
 * @param {string|number} b Number-like value
 * @param {?string|number} l Decimal length
 * @returns {string}
 */
function modulo(a, b, l) {
    return gdivide(a, b, l).modulus;
};

// Exports
module.exports = modulo;