"use strict";

// Imports
const isParsed = require("./isParsed.js");

/**
 * Formats a parsed number-like value
 * @param {[ "+"|"-", string, string ]} pv Parsed number-like value
 * @returns {string}
 */
function format(pv) {
    if(!isParsed(pv)) throw new TypeError("Argument is not a parsed number-like value");
    return `${pv[0]}${pv[1] || "0"}${pv[2] ? `.${pv[2]}` : ""}`;
};

// Exports
module.exports = format;