"use strict";

// Imports
const isInteger = require("../miscellaneous/isInteger.js");

/**
 * Returns if a number-like value is parsed correctly
 * @param {[ "+"|"-", string, string ]} pv Parsed number-like value
 * @returns {boolean}
 */
function isParsed(pv) {
    return [
        Array.isArray(pv),
        pv.length === 3,
        pv[0] === "+" || pv[0] === "-",
        isInteger(pv[1] || "0", true) && isInteger(pv[2] || "0", true)
    ].every(v => v === true);
};

// Exports
module.exports = isParsed;