"use strict";

// Imports
const isNumber = require("../miscellaneous/isNumber.js");

/**
 * Parses a number-like value
 * @param {string|number} v Number-like value
 * @param {boolean} t Whether or not to trim zeros
 * @returns {["+"|"-", string, string]}
 */
function parse(v, t) {
    if(!isNumber(v)) throw new TypeError("Argument is not a number-like value");
    let s = `${v}`.trim();
    if(!s.startsWith("+") && !s.startsWith("-")) s = `+${s}`;
    let i = s.slice(1).split(".")[0], d = s.includes(".") ? s.split(".")[1] : "";
    return [
        s.slice(0, 1),
        (t ? i.replace(/^0+/, "") : i) || "0",
        t ? d.replace(/0+$/, "") : d
    ];
};

// Exports
module.exports = parse;